mapper.select:
select ai.*, ifnull(ri.room_total, 0) total_room_count, ifnull(ri.room_total, 0) - ifnull(lg.rented_room, 0) free_room_count from (select id, name, introduction, district_id, district_name, city_id, city_name, province_id, province_name, address_detail, latitude, longitude, phone, is_release from lease.apartment_info
is_deleted = 0
and province_id = #{queryVo.provinceId}
and city_id = #{queryVo.cityId}
and district_id = #{queryVo.districtId}
) ai left join (select apartment_id, count(*) room_total from lease.room_info where is_deleted = 0 and is_release = 1 group by apartment_id) ri on ai.id = ri.apartment_id left join (select apartment_id, count(*) rented_room from lease.lease_agreement where is_deleted = 0 and status in (1, 5) group by apartment_id) lg on ai.id = lg.apartment_id
